import React from 'react';
import './LoginPage.css'; // Import the CSS file for styling
import logo from './pictures/logo.png';

function LoginPage() {
    const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Here you would typically verify the username and password
    // For this example, we'll assume the login is successful and navigate to /home
    navigate('/home');
  };

  return (
    <div className="login-container">
      <div className="logo-container">
        <img src="path/to/your/logo.png" alt="Company Logo" className="company-logo" />
      </div>
      <form className="login-form" onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          className="login-input"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          className="login-input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit" className="login-button">Login</button>
        <a href="#" className="forgot-password">Forgot Password?</a>
      </form>
    </div>
  );
};

export default LoginPage;
